//CRUD METHODS


/**
 * ResultService.create
 *   @description CRUD ACTION create
 *
 */
app.post(properties.api + '/results', function (req, res) {
	obj = new db_PowerCLILaunch_db.Result(req.body);
	obj.save(function (err) {
		if (err) return handleError(err, res);
		res.send(obj);
	});
});

/**
 * ResultService.delete
 *   @description CRUD ACTION delete
 *   @param ObjectId id Id
 *
 */
app['delete'](properties.api + '/results/:id', function (req, res) {
	db_PowerCLILaunch_db.Result.findByIdAndRemove(req.params.id, function (err) {
		  if (err) return handleError(err, res);
		  res.send(err);
	});
});

/**
 * ResultService.findByScriptExecution
 *   @description CRUD ACTION findByScriptExecution
 *   @param String key
 *
 */
app.get(properties.api + '/results/findByScriptExecution/:key', function (req, res) {

	db_PowerCLILaunch_db.Result.find({ 'ScriptExecution' : req.params.key}).exec(function (err, list) {
		if (err) return handleError(err, res);
		res.send(list);
	});
	
});

/**
 * ResultService.update
 *   @description CRUD ACTION update
 *   @param ObjectId id Id
 *
 */
app.post(properties.api + '/results/:id', function (req, res) {
	db_PowerCLILaunch_db.Result.findByIdAndUpdate(req.params.id, req.body, {'new': true}, function (err, obj) {
		if (err) return handleError(err, res);
		res.send(obj);
	});
});


